<MDXTag name="p" components={components}>{`Let's build harder part, transferring money from current user to somebody else:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`export function transfer(to: string, value: u64): boolean {
    let balance = balanceOf(contractContext.sender);
    if value > balance {
        return false;
    }
    globalStorage.setU64(balanceKey(contractContext.sender), balance - value);
    globalStorage.setU64(to, globalStorage.getU64(to) + value);
